mapper.update:
update boardMaster
   set b_hit = b_hit + 1
 where b_no =#{b_no}

mapper.insert:
INSERT INTO boardmaster(b_no, b_name, b_title, b_content
                       ,b_group, b_pos, b_step
                       ,b_date, b_pwd)
                 VALUES(#{b_no}, #{b_name}, #{b_title}, #{b_content}
                       ,#{b_group}, #{b_pos}, #{b_step}
                       ,to_char(sysdate, 'YYYY-MM-DD'),#{b_pwd})

mapper.update:
update boardMaster
      set b_title = #{b_title}
         ,b_name = #{b_name}
         ,b_content = #{b_content}
         ,b_pwd = #{b_pwd}
  where b_no = #{b_no}

update boardMaster
      set b_step = b_step+1
  where b_group = #{b_group} 
     and b_step > #{b_step}

mapper.select:
SELECT a.b_no, a.b_title, a.b_name, a.b_content ,a.b_date, a.b_hit, NVL(a.b_file,'없음') b_file, a.b_pwd ,a.b_group, a.b_pos, a.b_step, a.rno FROM ( SELECT bm.b_no, bm.b_title, bm.b_name, bm.b_content ,bm.b_date, bm.b_hit, NVL(bs.b_file,'없음') b_file, bm.b_pwd ,bm.b_group, bm.b_pos, bm.b_step, rownum rno FROM boardMaster bm, boardSub bs WHERE bm.b_no = bs.b_no(+) )a
AND a.b_no = #{b_no}
AND a.b_title LIKE '%'||#{sb_keyword}||'%'
AND a.b_content LIKE '%'||#{sb_keyword}||'%'
AND a.b_name LIKE '%'||#{sb_keyword}||'%'
AND a.rno >= #{start}
AND a.rno <= #{end}
ORDER BY a.b_group desc, a.b_step asc
